"use strict";

var sql = require('mssql');

var datos = require('../config');

var config = {
  "user": datos.user,
  "password": datos.pass,
  "server": datos.server,
  "database": datos.database,
  options: {
    trustServerCertificate: true
  },
  pool: {
    max: 5,
    min: 0
  }
};
sql.connect(config, function (err) {
  if (err) {
    throw err;
  }

  console.log("Conexion realizada!");
});
module.exports = sql;
//# sourceMappingURL=data:application/json;charset=utf-8;base64,

sourcesContent:
const sql = require('mssql');
const datos = require('../config')
var config = {
  "user": datos.user,
  "password": datos.pass,
  "server": datos.server, 
  "database": datos.database, 
  options: { 
      trustServerCertificate: true,
      },
      pool: {
        max: 5,
        min: 0
      }
}
sql.connect(config, err => { 
  if(err){
      throw err ;
  }
  console.log("Conexion realizada!")     
});
module.exports = sql;

